  }

  .btn-login {
    background: linear-gradient(90deg, #ff007f, #00ffff);
    color: white;
    font-size: 1rem;
    padding: 10px 20px;
    border: none;
    border-radius: 8px;
    cursor: pointer;
    transition: transform 0.3s ease-in-out, box-shadow 0.3s ease-in-out;
