import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import { BASE_API_URL, APP_NAME } from "./globals";
const MemberInsert = () => {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [sex, setSex] = useState("");
  const [birthday, setBirthday] = useState("");
  const [phone, setPhone] = useState("");
  const [address, setAddress] = useState("");
  const [zipcode, setZipcode] = useState("");
  const [country, setCountry] = useState("");
  const navigate = useNavigate();

  const handleAdd = async (e) => {
    e.preventDefault();
    if (!validateForm()) {
      return;
    }
    try {
      await axios.post(BASE_API_URL + "/membersadd", {
        name_mem: name,
        email_mem: email,
        password_mem: password,
        sex_mem: sex,
        birthday_mem: birthday,
        phone_mem: phone,
        address_mem: address,
        zipcode_mem: zipcode,
        country_mem: country,
      });
      alert("Member added successfully");
      navigate("/search");
    } catch (error) {
      alert("Failed to add member");
    }
  };

  const handlecancel = () => {
    navigate("/search");
  };

  const validateForm = () => {
    if (!name || !email || !password || !birthday || !phone || !address || !zipcode || !country || !sex) {
      alert("กรุณากรอกข้อมูลที่จำเป็น");
      return false;
    }
    return true;
  };

  return (
    <div className="member-insert-container">
      <div className="content-wrapper">
        <h2>Add Member</h2>
        <form onSubmit={handleAdd}>
          <div>
            <label>Name:</label>
            <input
              type="text"
              value={name}
              onChange={(e) => setName(e.target.value)}
              required
            />
          </div>
          <div>
            <label>Email:</label>
            <input
              type="email"
              placeholder="ponglert.s@ubru.ac.th"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </div>
          <div>
            <label>Password:</label>
            <input
              type="text"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </div>
          <div>
            <label>Sex:</label>
            <select value={sex} onChange={(e) => setSex(e.target.value)}>
              <option value="">เลือกเพศ</option>
              <option value="1">ชาย</option>
              <option value="2">หญิง</option>
            </select>
          </div>
          <div>
            <label>Birthday:</label>
            <input
              type="date"
              value={birthday}
              onChange={(e) => setBirthday(e.target.value)}
              required
            />
          </div>
          <div>
            <label>Phone:</label>
            <input
              type="text"
              value={phone}
              onChange={(e) => setPhone(e.target.value)}
              required
            />
          </div>
          <div>
            <label>Address:</label>
            <textarea
              type="text"
              value={address}
              onChange={(e) => setAddress(e.target.value)}
              rows="5"
              required
            />
          </div>
          <div>
            <label>Zipcode:</label>
            <input
              type="text"
              value={zipcode}
              onChange={(e) => setZipcode(e.target.value)}
              required
            />
          </div>
          <div>
            <label>Country:</label>
            <input
              type="text"
              value={country}
              onChange={(e) => setCountry(e.target.value)}
              required
            />
          </div>
          <div className="button-container">
            <button type="submit" className="save-button">Save</button>
            <button type="button" className="cancel-button" onClick={() => handlecancel()}>Cancel</button>
          </div>
        </form>
      </div>

      <style jsx>{`
        .member-insert-container {
          display: flex;
          justify-content: center;
          align-items: center;
          min-height: 100vh;
          background: url("https://i.pinimg.com/originals/77/ca/a3/77caa32884d735d439ade45ba37feaf2.gif")
            no-repeat center center fixed;
          background-size: cover;
          overflow: hidden;
          background-attachment: fixed;
        }

        .content-wrapper {
          background: rgba(255, 255, 255, 0.3);
          padding: 2rem;
          border-radius: 10px;
          box-shadow: 0 0 10px rgba(80, 18, 160, 0.6);
          width: 100%;
          max-width: 600px;
          position: fixed;
          overflow: auto;
          max-height: 90vh;
        }

        h2 {
          margin-bottom: 1.5rem;
          color: #fff;
          text-align: center;
        }

        form div {
          margin-bottom: 1rem;
        }

        label {
          display: block;
          margin-bottom: 0.5rem;
          color: #fff;
        }

        input, select, textarea {
          width: 100%;
          padding: 0.75rem;
          border: 1px solid #444;
          border-radius: 5px;
          font-size: 1rem;
          background: #333;
          color: #fff;
          transition: transform 0.3s, box-shadow 0.3s;
        }

        input:focus, select:focus, textarea:focus {
          transform: scale(1.02);
          box-shadow: 0 0 10px rgba(33, 70, 238, 0.99);
        }

        .button-container {
          display: flex;
          justify-content: center;
          gap: 1rem;
          margin-top: 2rem;
        }

        .save-button,
        .cancel-button {
          width: calc(40% - 1.5rem);
          padding: 0.75rem;
          background: rgba(7, 75, 221, 0.67);
          box-shadow: 0 0 8px rgba(105, 9, 230, 0.6);
          border: none;
          border-radius: 5px;
          color: white;
          font-size: 1rem;
          cursor: pointer;
          transition: background 0.3s, transform 0.3s;
        }

        .save-button:hover,
        .cancel-button:hover {
          background: rgba(64, 120, 241, 0.73);
          transform: scale(1.05);
        }

        textarea {
          resize: vertical;
          min-height: 100px;
        }

        select {
          cursor: pointer;
        }

        @keyframes float {
          0% { transform: translateY(0); }
          50% { transform: translateY(-10px); }
          100% { transform: translateY(0); }
        }
      `}</style>
    </div>
  );
};
export default MemberInsert;
