import React, { useState, useEffect } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import axios from "axios";
import { BASE_API_URL, APP_NAME } from "./globals";
// import '../style/MemberSearch.css'; // Import the CSS file
// const user = JSON.parse(localStorage.getItem("user")) || {};

const MemberSearch = () => {
  const location = useLocation();
  const navigate = useNavigate(); // Hook for navigation
  const [name, setName] = useState("");
  const [results, setResults] = useState([]);
  const user = JSON.parse(localStorage.getItem("user")) || {};

  const handleSearch = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post(BASE_API_URL + "/members/search", {
        name_mem: name,
      });
      setResults(response.data);
    } catch (error) {
      alert("❌ Search failed");
    }
  };

  const handleSearchReload = async () => {
    try {
      const response = await axios.post(BASE_API_URL + "/members/search", {
        name_mem: name,
      });
      setResults(response.data);
    } catch (error) {
      alert("❌ Search failed");
    }
  };

  useEffect(() => {
    handleSearchReload(); // Trigger search when the component mounts
  }, []);

  const handleEdit = (id_mem) => {
    navigate("/edit", {
      state: { id: id_mem },
    });
  };

  const handleAdd = () => {
    navigate("/insert");
  };

  const handleLogout = () => {
    localStorage.removeItem("isLoggedIn");
    localStorage.removeItem("user");
    window.location.href = "/";
  };

  return (
    <div className="member-search-container">
      <h2>{APP_NAME} 📋</h2>
      <p>Welcome, {user?.name_mem || "User"}! 👋</p>
      <form className="search-form" onSubmit={handleSearch}>
        <input
          type="text"
          placeholder="Enter name"
          value={name}
          onChange={(e) => setName(e.target.value)}
        />
        <button type="submit" className="search">
          🔍 Search
        </button>
      </form>
      <button className="add-button" onClick={() => handleAdd()}>➕ Add</button>
      <button className="logout-button" onClick={handleLogout}>Logout</button>
      <table>
        <thead>
          <tr>
            <th>ID ผู้ใช้</th>
            <th>ชื่อ-นามสกุล</th>
            <th>เบอร์โทร</th>
            <th>E-mail</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {results.map((member) => (
            <tr key={member.id_mem}>
              <td>{member.id_mem}</td>
              <td>{member.name_mem}</td>
              <td>{member.phone_mem}</td>
              <td>{member.email_mem}</td>
              <td>
                <button className="edit-button" onClick={() => handleEdit(member.id_mem)}>
                  ✏️ Edit
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      <style jsx>{`
        .member-search-container {
          display: flex;
          flex-direction: column;
          align-items: center;
          height: 100vh;
          background: url("https://img.itch.zone/aW1nLzEzNzczMjU3LmdpZg==/original/W%2Fd8%2BO.gif")
            no-repeat center center fixed;
          background-size: cover;
          animation: backgroundMotion 10s infinite alternate;
        }

        h2 {
          margin-bottom: 1.5rem;
          color: #fff;
        }

        p {
          color: #ccc;
        }

        .search-form {
          display: flex;
          flex-direction: column;
          align-items: center;
          margin-bottom: 1.5rem;
        }

        .search-form input {
          width: calc(100% - 1.5rem);
          padding: 0.75rem;
          margin-bottom: 1rem;
          border: 1px solid #444;
          border-radius: 5px;
          font-size: 1rem;
          background: #333;
          color: #fff;
          transition: transform 0.3s, box-shadow 0.3s;
        }

        .search-form input:focus {
          transform: scale(1.05);
          box-shadow: 0 0 10px rgba(255, 255, 255, 0.5);
        }

        .search-form button {
          width: calc(100% - 1.5rem);
          padding: 0.75rem;
          background: rgb(49, 210, 221);
          border: none;
          border-radius: 5px;
          color: white;
          font-size: 1rem;
          cursor: pointer;
          transition: background 0.3s, transform 0.3s;
        }

        .search-form button:hover {
          background: rgb(34, 110, 116);
          transform: scale(1.05);
        }

        .add-button,
        .logout-button {
          width: calc(100% - 1.5rem);
          padding: 0.75rem;
          margin-top: 1rem;
          background: rgb(49, 210, 221);
          border: none;
          border-radius: 5px;
          color: white;
          font-size: 1rem;
          cursor: pointer;
          transition: background 0.3s, transform 0.3s;
        }

        .add-button:hover,
        .logout-button:hover {
          background: rgb(34, 110, 116);
          transform: scale(1.05);
        }

        table {
          width: 100%;
          max-width: 800px;
          margin-top: 1.5rem;
          border-collapse: collapse;
          background: rgba(0, 0, 0, 0.7);
          color: #fff;
        }

        th, td {
          padding: 0.75rem;
          border: 1px solid #444;
          text-align: left;
        }

        th {
          background: #333;
        }

        .edit-button {
          padding: 0.5rem 1rem;
          background: rgb(49, 210, 221);
          border: none;
          border-radius: 5px;
          color: white;
          font-size: 1rem;
          cursor: pointer;
          transition: background 0.3s, transform 0.3s;
        }

        .edit-button:hover {
          background: rgb(34, 110, 116);
          transform: scale(1.05);
        }

        @keyframes float {
          0% {
            transform: translateY(0);
          }
          50% {
            transform: translateY(-10px);
          }
          100% {
            transform: translateY(0);
          }
        }

        @keyframes backgroundMotion {
          0% {
            background-position: center;
          }
          100% {
            background-position: top;
          }
        }
      `}</style>
    </div>
  );
};

export default MemberSearch;
