import React, { useState } from "react";
import { motion } from "framer-motion";
import { User, Lock, Rocket } from "lucide-react";

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const handleLogin = (e) => {
    e.preventDefault();
    // Login logic here
  };

  return (
    <div className="min-h-screen w-full bg-[url('/api/placeholder/1920/1080')] bg-cover bg-center relative overflow-hidden">
      {/* Starfield Overlay */}
      <div className="absolute inset-0 bg-black/50 backdrop-blur-sm">
        <div className="absolute inset-0 bg-gradient-to-br from-purple-900/20 to-blue-900/20" />
      </div>

      {/* Animated Stars */}
      <div
        className="absolute inset-0"
        style={{
          background:
            "radial-gradient(circle at center, transparent 0%, transparent 100%)",
          boxShadow:
            "inset 0 0 50px #fff, inset 20px 0 80px #f0f, inset -20px 0 80px #0ff",
          animation: "stars 8s linear infinite",
        }}
      />

      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        className="relative min-h-screen flex items-center justify-center p-4"
      >
        <motion.form
          onSubmit={handleLogin}
          className="w-96 bg-black/30 backdrop-blur-xl rounded-2xl p-8 border border-white/10"
          whileHover={{ scale: 1.02 }}
          transition={{ type: "spring", stiffness: 300 }}
        >
          <motion.div
            className="flex items-center justify-center mb-8"
            whileHover={{ rotate: 360 }}
            transition={{ duration: 0.5 }}
          >
            <Rocket className="w-12 h-12 text-cyan-400" />
          </motion.div>

          <h2 className="text-3xl font-bold text-white mb-8 text-center bg-gradient-to-r from-pink-500 via-cyan-400 to-purple-500 bg-clip-text text-transparent">
            Login Portal
          </h2>

          <div className="space-y-6">
            <motion.div whileHover={{ scale: 1.02 }} className="relative">
              <User className="absolute left-3 top-3 h-5 w-5 text-cyan-400" />
              <input
                type="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                className="w-full bg-white/5 border border-white/10 rounded-lg pl-10 p-3 text-white focus:border-cyan-400 focus:ring-2 focus:ring-cyan-400/50 transition-all"
                placeholder="Enter your email"
              />
            </motion.div>

            <motion.div whileHover={{ scale: 1.02 }} className="relative">
              <Lock className="absolute left-3 top-3 h-5 w-5 text-cyan-400" />
              <input
                type="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                className="w-full bg-white/5 border border-white/10 rounded-lg pl-10 p-3 text-white focus:border-cyan-400 focus:ring-2 focus:ring-cyan-400/50 transition-all"
                placeholder="Enter your password"
              />
            </motion.div>

            <motion.button
              type="submit"
              className="w-full bg-gradient-to-r from-cyan-400 to-purple-500 text-white rounded-lg p-3 font-semibold"
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
            >
              Launch
            </motion.button>
          </div>

          <motion.p
            className="mt-6 text-center text-white/60"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ delay: 0.5 }}
          >
            Not registered?{" "}
            <a href="#" className="text-cyan-400 hover:text-cyan-300">
              Create account
            </a>
          </motion.p>
        </motion.form>
      </motion.div>

      <style jsx global>{`
        @keyframes stars {
          0% {
            transform: rotate(0deg);
          }
          100% {
            transform: rotate(360deg);
          }
        }
      `}</style>
    </div>
  );
};

export default Login;
