import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import { BASE_API_URL } from "./globals";

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post(BASE_API_URL + "/login", {
        email_mem: email,
        password_mem: password,
      });
      alert("üéâ Login Successful");
      localStorage.setItem("isLoggedIn", "true");
      localStorage.setItem("user", JSON.stringify(response.data.user));
      navigate("/search");
    } catch (error) {
      alert(error.response?.data || "‚ùå Login Failed");
    }
  };

  return (
    <div className="login-container">
      <form className="login-form" onSubmit={handleLogin}>
        <h1 className="title">Welcome Back!</h1>
        <p className="subtitle">Sign in to explore the future</p>
        <div className="input-container">
          <label className="label">Email</label>
          <input
            type="email"
            className="input"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
        </div>
        <div className="input-container">
          <label className="label">Password</label>
          <input
            type="password"
            className="input"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </div>
        <button type="submit" className="btn-login">
          Login
        </button>
      </form>
    </div>
  );
};

export default Login;

// Styles in CSS or SCSS
const styles = `
  .login-container {
    display: flex;
    justify-content: center;
    align-items: center;
    height: 100vh;
    background: linear-gradient(to bottom right, #121212, #1f1f1f);
    color: #e0e0e0;
    font-family: 'Roboto', sans-serif;
  }

  .login-form {
    background: rgba(255, 255, 255, 0.1);
    backdrop-filter: blur(10px);
    padding: 40px;
    border-radius: 12px;
    box-shadow: 0 8px 32px rgba(0, 0, 0, 0.37);
    display: flex;
    flex-direction: column;
    align-items: center;
  }

  .title {
    font-size: 2.5rem;
    margin-bottom: 1rem;
  }

  .subtitle {
    font-size: 1.2rem;
    margin-bottom: 2rem;
    color: #bbb;
  }

  .input-container {
    width: 100%;
    margin-bottom: 1.5rem;
  }

  .label {
    display: block;
    margin-bottom: 0.5rem;
    font-size: 1rem;
    font-weight: bold;
  }

  .input {
    width: 100%;
    padding: 0.75rem;
    border: none;
    border-radius: 8px;
    background: rgba(255, 255, 255, 0.15);
    color: #fff;
    font-size: 1rem;
  }

  .btn-login {
    padding: 0.75rem 2rem;
    border: none;
    border-radius: 8px;
    background: linear-gradient(90deg, #6a11cb, #2575fc);
    color: white;
    font-size: 1rem;
    cursor: pointer;
    transition: background 0.3s;
  }

  .btn-login:hover {
    background: linear-gradient(90deg, #2575fc, #6a11cb);
  }
`;

document.head.appendChild(
  Object.assign(document.createElement("style"), { textContent: styles })
);
